internal import SwiftUI

extension Color {
    // Note: buttonOverlay, sheetBackground, overlayDim, and border (BorderColor)
    // are auto-generated from Assets.xcassets by Xcode

    // Semantic colors for consistent theming
    static var adaptiveBackground: Color {
        Color(UIColor.systemBackground)
    }

    static var adaptiveSecondaryBackground: Color {
        Color(UIColor.secondarySystemBackground)
    }

    static var adaptiveGroupedBackground: Color {
        Color(UIColor.systemGroupedBackground)
    }

    static var adaptiveLabel: Color {
        Color(UIColor.label)
    }

    static var adaptiveSecondaryLabel: Color {
        Color(UIColor.secondaryLabel)
    }
}
